up.guard_js:
const fee = 5;
if (world.has('gold', fee)) {
  world.pay('gold', fee, 'lodging fee', 'character/fictional/marieke');
  return true;
}
return false;

bar.accept_message_js:
const tags = world.player.tags || [];
if (tags.includes('staff')) return "Staff access granted. You slip behind the bar.";
if (tags.includes('cat')) return "The cat flap swings open. Cats rule the bar.";
if (tags.includes('dog')) return "The dogs have earned their spot behind the bar.";
if (tags.includes('monkey')) return "Palm climbs gracefully over the bar. Royalty has privileges.";
return "Access granted.";

appear.implementation:
# Check if scene has controlled elements
if scene.is_too_planned():
    return self.definitely_appear()
else:
    return self.probably_appear_anyway()

SIT.effect:
